Invoke-Command -ComputerName HC-DC12 -ScriptBlock {clear; $ip = Read-Host -Prompt "Digite o IP";if($ip -Like "192.168.*"){}elseif($ip -Like "HC*"){}else{Continue}; Get-WmiObject -ComputerName $ip -ClassName win32_ComputerSystem -ErrorAction SilentlyContinue | ft @{Name="Hostname";Expression={$_.Name}}; Test-Connection -ComputerName $ip -Count 1 -ErrorAction SilentlyContinue | ft @{Name="Endereco IP";Expression={$_.IPV4Address}}; Get-WmiObject -ComputerName $ip -Class Win32_Computersystem | ft @{Name="Usuario Logado";Expression={$_.Username}}; Get-WmiObject -ComputerName $ip -Class Win32_Baseboard | ft @{Name="PLACA MAE";Expression={$_.Product}}; Get-WmiObject -ComputerName $ip -Class Win32_Processor | ft @{Name="Processador";Expression={$_.Name}}; Get-WmiObject -ComputerName $ip -Class Win32_OperatingSystem | ft @{Name="Arquitetura OS";Expression={$_.OSArchitecture}}; Get-WmiObject -ComputerName $ip -Class Win32_VideoController | ft @{Name="Placa de Video";Expression={$_.Name}}; Get-WmiObject -ComputerName $ip -Class Win32_PhysicalMemory | ft @{Name="RAM Slot";Expression={$_.BankLabel}},@{Name="Tamanho (GB)";Expression={[math]::round($_.Capacity/1GB,0)}}; Get-WmiObject -ComputerName $ip -Class Win32_LogicalDisk | ? DeviceID -Like *C* | ft @{Name="Disco";Expression={$_.Name}},@{Name="Livre (GB)";Expression={[math]::round($_.FreeSpace/1GB, 0)}},@{Name="Tamanho (GB)";Expression={[math]::round($_.Size/1GB, 0)}}; pause}
